import React, { useState } from 'react';
import { motion } from 'framer-motion';

const LOGO_IMG = "data:image/png;base64,";

export function PodcastLogo({ size = 'md', className = '' }) {
    const [isHovered, setIsHovered] = useState(false);

    const sizeConfig = {
        sm: {
            wrapper: 'w-20 h-20',
            svgSize: 80,
            radius: 34,
            cx: 40, cy: 40,
            pathId: 'sm-path',
            fontSize: 4.8,
            letterSpacing: 2.4,
            pathD: 'M 40,40 m -34,0 a 34,34 0 1,1 68,0 a 34,34 0 1,1 -68,0',
            innerPad: 14,
            imgPad: 4,
        },
        md: {
            wrapper: 'w-44 h-44',
            svgSize: 176,
            radius: 75,
            cx: 88, cy: 88,
            pathId: 'md-path',
            fontSize: 9.5,
            letterSpacing: 3.5,
            pathD: 'M 88,88 m -75,0 a 75,75 0 1,1 150,0 a 75,75 0 1,1 -150,0',
            innerPad: 28,
            imgPad: 10,
        },
        lg: {
            wrapper: 'w-64 h-64',
            svgSize: 256,
            radius: 108,
            cx: 128, cy: 128,
            pathId: 'lg-path',
            fontSize: 13,
            letterSpacing: 4.5,
            pathD: 'M 128,128 m -108,0 a 108,108 0 1,1 216,0 a 108,108 0 1,1 -216,0',
            innerPad: 38,
            imgPad: 14,
        },
    };

    const cfg = sizeConfig[size] || sizeConfig.md;
    const pauseStyle = isHovered ? { animationPlayState: 'paused' } : {};

    return (
        <motion.div
            initial={{ opacity: 0, scale: 0.75 }}
            animate={{ opacity: 1, scale: 1 }}
            transition={{ duration: 1, ease: [0.16, 1, 0.3, 1] }}
            className={`relative ${cfg.wrapper} flex-shrink-0 cursor-pointer select-none ${className}`}
            onMouseEnter={() => setIsHovered(true)}
            onMouseLeave={() => setIsHovered(false)}
            tabIndex={0}
            onFocus={() => setIsHovered(true)}
            onBlur={() => setIsHovered(false)}
            role="img"
            aria-label="Podcast Vault logo"
        >
            {/* Deep space background */}
            <div
                className="absolute inset-0 rounded-full"
                style={{
                    background: 'radial-gradient(ellipse at 35% 30%, #1a1030 0%, #08080f 55%, #030308 100%)',
                    boxShadow: '0 0 60px #7b3fbe33, 0 0 120px #3a1f6611, inset 0 0 40px #00000099',
                }}
            />

            {/* Outer glow pulse ring */}
            <motion.div
                animate={{ scale: [1, 1.04, 1], opacity: [0.4, 0.7, 0.4] }}
                transition={{ duration: 3.5, repeat: Infinity, ease: 'easeInOut' }}
                className="absolute inset-0 rounded-full pointer-events-none"
                style={{
                    boxShadow: '0 0 32px #c9a96e99, 0 0 64px #c9a96e99',
                    border: '1.5px solid #9d6de855',
                }}
            />

            {/* Rotating orbit ring */}
            <motion.div
                animate={{ rotate: 360 }}
                transition={{ duration: 18, repeat: Infinity, ease: 'linear' }}
                className="absolute inset-0 rounded-full pointer-events-none"
                style={{
                    border: '1px solid transparent',
                    borderTopColor: '#c9a96e99',
                    borderRightColor: '#7b3fbe55',
                    borderBottomColor: '#4fc3f733',
                    borderLeftColor: '#c9a96e22',
                    filter: 'drop-shadow(0 0 6px #c9a96e88)',
                    ...pauseStyle,
                }}
            />

            {/* Rotating text SVG */}
            <motion.svg
                animate={{ rotate: 360 }}
                transition={{ duration: 22, repeat: Infinity, ease: 'linear' }}
                style={{ ...pauseStyle }}
                className="absolute inset-0 w-full h-full pointer-events-none"
                viewBox={`0 0 ${cfg.svgSize} ${cfg.svgSize}`}
                xmlns="http://www.w3.org/2000/svg"
                aria-hidden="true"
            >
                <defs>
                    <path id={cfg.pathId} d={cfg.pathD} />
                    <filter id="text-glow">
                        <feGaussianBlur stdDeviation="1.5" result="blur" />
                        <feMerge>
                            <feMergeNode in="blur" />
                            <feMergeNode in="SourceGraphic" />
                        </feMerge>
                    </filter>
                </defs>
                <text
                    fontFamily="'Cormorant Garamond', Georgia, serif"
                    fontSize={cfg.fontSize}
                    fontWeight="600"
                    fill="#c9a96e"
                    letterSpacing={cfg.letterSpacing}
                    filter="url(#text-glow)"
                    opacity="0.85"
                >
                    <textPath href={`#${cfg.pathId}`} startOffset="0%">
                        OPINION  NOT FACT  PERSPECTIVE  NOT TRUTH 
                    </textPath>
                </text>
            </motion.svg>

            {/* Inner circle container for image */}
            <div
                className="absolute rounded-full flex items-center justify-center overflow-hidden z-10"
                style={{
                    inset: cfg.innerPad,
                    background: 'radial-gradient(ellipse at 40% 25%, #1c1040 0%, #080810 60%, #020205 100%)',
                    border: '1px solid #c9a96e99',
                    boxShadow: 'inset 0 0 30px #00000088, 0 0 20px #7b3fbe22',
                }}
            >
                {/* Subtle inner glow rim */}
                <div
                    className="absolute inset-0 rounded-full pointer-events-none"
                    style={{
                        background: 'radial-gradient(ellipse at 50% 0%, #c9a96e12 0%, transparent 60%)',
                    }}
                />

                {/* The microphone image */}
                <motion.img
                    src={LOGO_IMG}
                    alt="Podcast Vault microphone"
                    initial={{ opacity: 0, scale: 0.8, y: 6 }}
                    animate={{ opacity: 1, scale: 1, y: 0 }}
                    transition={{ delay: 0.5, duration: 0.9, ease: [0.16, 1, 0.3, 1] }}
                    style={{
                        position: 'absolute',
                        width: '100%',
                        height: '100%',
                        objectFit: 'cover',
                        objectPosition: 'center center',
                        filter: 'drop-shadow(0 0 14px #ffffffcc) drop-shadow(0 0 28px #4fc3f766) brightness(1.08)',
                    }}
                />
            </div>

            {/* Hover pause label */}
            {isHovered && (
                <motion.div
                    initial={{ opacity: 0, y: 4 }}
                    animate={{ opacity: 1, y: 0 }}
                    className="absolute -bottom-7 left-1/2 -translate-x-1/2 text-xs whitespace-nowrap pointer-events-none"
                    style={{ color: '#c9a96e99', fontFamily: 'Cormorant Garamond, serif', letterSpacing: '0.12em' }}
                    aria-hidden="true"
                >
                     PAUSED 
                </motion.div>
            )}
        </motion.div>
    );
}

export default PodcastLogo;